import React from 'react';
import '../../App.css';
import { makeStyles } from '@material-ui/styles';
import { TextField, Box, Button } from '@material-ui/core';
import { useTheme } from '@material-ui/core/styles';
//import apiClient from "./http-common";
import { useState, useEffect } from 'react';
import { useMutation } from 'react-query';

const useStyles = makeStyles((theme) =>  ({
    grow: {
      flexGrow:1,
    },
    textBox: {
        paddingBottom:theme.spacing(2),
    },
    buttonContinue: {
        flexGrow: 2,
        paddingTop: theme.spacing(2),
        color:"#fff",
        background: "#26319a",
    },
}));

function Registration() {

    const classes = useStyles();
    const theme = useTheme();


    const { isLoading, isSuccess, isError, data, error, mutate } =
    useMutation(postParticipant(info), { onSuccess, onError });

    const [postFirstName, setPostFirstName] = useState("");
    const [postLastName, setPostLastName] = useState("");
    const [postNameCompany, setPostNameCompany] = useState("");
    const [postOffice, setPostOffice] = useState("");
    const [postEmail, setPostEmail] = useState("");
    const [postPhone, setPostPhone] = useState("");
    const [postPassword, setPostPassword] = useState("");
    const [postCheckPassword, setPostCheckPassword] = useState("");


      const { isLoading: isPostingParticipant, mutate: postParticipant } = useMutation(
        async () => {
          return await apiClient.post(`/participant`, {
            firstName: postFirstName,
            lastName: postLastName,
            companyName: postNameCompany,
            job: postOffice,
            email:postEmail,
            phone:postPhone,
            password:postPassword,
          });
        },

      );

      useEffect(() => {
        if (isPostingParticipant) setPostResult("posting...");
      }, [isPostingParticipant]);

      function postData() {
        try {
          postParticipant();
        } catch (err) {
          //setPostResult(fortmatResponse(err));
          alert(err);
        }
      }


    return (
        <div className = "formulario">
            <br />
            <h2>Preencha os dados</h2>
             <Box className ="boxRegister" p={8} sx={{width: 500, maxWidth: '100%',}}>

               <TextField 
                className = {classes.textBox}
                value={postFirstName}
                onChange={(e) => setPostFirstName(e.target.value)}
                fullWidth 
                label="Nome" 
                id="firstName" 
               />
               <TextField 
                className = {classes.textBox}
                value={postLastName}
                onChange={(e) => setPostLastName(e.target.value)}
                fullWidth 
                label="Sobrenome" 
                id="fullWidth" 
               />
               <TextField 
                className = {classes.textBox}
                value={postNameCompany}
                onChange={(e) => setPostNameCompany(e.target.value)}
                fullWidth 
                label="Nome da empresa"
                id="fullWidth" 
               />
               <TextField 
                className = {classes.textBox} 
                value={postOffice}
                onChange={(e) => setPostOffice(e.target.value)}
                fullWidth 
                label="Cargo" 
                id="fullWidth" 
               />
               <TextField 
                className = {classes.textBox} 
                value={postEmail}
                onChange={(e) => setPostEmail(e.target.value)}
                fullWidth
                label="E-mail corporativo" 
                id="fullWidth"
               />
               <TextField 
                className = {classes.textBox} 
                fullWidth
                label="Telefone" 
                id="fullWidth" 
               />
               <TextField 
                className = {classes.textBox}
                value={postPassword}
                onChange={(e) => setPostPassword(e.target.value)}
                fullWidth 
                label="Senha" 
                id="fullWidth" 
               /> 
               <TextField 
                className = {classes.textBox} 
                value={postCheckPassword}
                onChange={(e) => setPostCheckPassword(e.target.value)}
                fullWidth
                label="Confirmar senha" 
                id="fullWidth" 
               />
                  <Button 
                    className= {classes.buttonContinue} 
                    variant="contained" 
                    id = "buttonForm" 
                    onClick={postData}
                  >
                    Continuar
                  </Button>
             </Box>
        </div>
    );
}

export default Registration;
