import React from 'react';
import '../../App.css';
//import { makeStyles } from '@material-ui/styles';
import { TextField, Box, Button, Divider } from '@material-ui/core';
//import { useTheme } from '@material-ui/core/styles';
import api from './http-common';
//import { useQuery } from "react-query";
import { useState, useEffect } from 'react';
import { useMutation } from 'react-query';
import axios from 'axios';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import Radio from '@mui/material/Radio';
import { styled } from '@mui/material/styles';
import HelpOutlineSharpIcon from '@mui/icons-material/HelpOutlineSharp';

const Item = styled(Paper)(({ theme }) => ({
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

// const useStyles = makeStyles((theme) =>  ({
//     grow: {
//       flexGrow:1,
//     },

//     buttonContinue: {
//         flexGrow: 2,
//         paddingTop: theme.spacing(2),
//         color:"#fff",
//         background: "#26319a",
//     },
// }));

function Registration() {

            //const classes = useStyles();
            //const theme = useTheme();
            const [chose, setChose] = useState(null);

            const alertHelp = () => {
               return alert("Você deve escolher" + "\n" +"a versão que deseja participar do evento." + "\n" + "Se VR, fique atento ao seu regulamento de uso!");
            }

            const [postFirstName, setPostFirstName] = useState("");
            const [postLastName, setPostLastName] = useState("");
            const [postNameCompany, setPostNameCompany] = useState("");
            const [postOffice, setPostOffice] = useState("");
            const [postEmail, setPostEmail] = useState("");
            const [postPhone, setPostPhone] = useState("");
            const [postPassword, setPostPassword] = useState("");
            const [postCheckPassword, setPostCheckPassword] = useState("");




    return (
        <div className = "formulario">
            <br />
           <div className="boxRegisterWhite"> 
             <br /><br /><br />
             <Box className ="boxRegister" p={8} sx={{width: 600, maxWidth: '100%',}}>
                <h2>Preencha todos os campos </h2>
                <br />
                <br />
                <br />
               <TextField 
                //className = {classes.textBox}
                value={postFirstName}
                onChange={(e) => setPostFirstName(e.target.value)}
                size="small"
                fullWidth 
                label="Nome" 
                id="firstName" 
               />
               <br />
               <br />
               <TextField 
                //className = {classes.textBox}
                value={postLastName}
                onChange={(e) => setPostLastName(e.target.value)}
                size="small"
                fullWidth 
                label="Sobrenome" 
                id="fullWidth" 
               />
               <br />
               <br />
               <TextField 
                //className = {classes.textBox}
                value={postNameCompany}
                onChange={(e) => setPostNameCompany(e.target.value)}
                size="small"
                fullWidth 
                label="Nome da empresa"
                id="fullWidth" 
               />
               <br />
               <br />
               <TextField 
                //className = {classes.textBox} 
                value={postOffice}
                onChange={(e) => setPostOffice(e.target.value)}
                size="small"
                fullWidth 
                label="Cargo" 
                id="fullWidth" 
               />
               <br />
               <br />
               <TextField 
                //className = {classes.textBox} 
                value={postEmail}
                onChange={(e) => setPostEmail(e.target.value)}
                size="small"
                fullWidth
                label="E-mail corporativo" 
                id="fullWidth"
               />
               <br />
               <br />
               <TextField 
                //className = {classes.textBox} 
                value={postPhone}
                onChange={(event) => setPostPhone(event.target.value) }
                size="small"
                fullWidth
                label="Telefone" 
                id="fullWidth" 
               />
               <br />
               <br />
               <TextField 
                //className = {classes.textBox}
                value={postPassword}
                type="password"
                size="small"
                onChange={(e) => setPostPassword(e.target.value)}
                fullWidth 
                label="Senha" 
                id="fullWidth" 
               /> 
               <br />
               <br />
               <TextField 
                //className = {classes.textBox} 
                value={postCheckPassword}
